INSERT INTO `news` (`id`, `title`, `text`, `date`, `image`, `created_at`, `updated_at`, `visible`)
VALUES
	(1,'Yuki Officialリニューアルオープン！','<p>Yuki Official Websiteがリニューアルしました！<br />\r\n<br />\r\n引き続きYukiの活動をこちらからお知らせします。 セットリスト一覧やMr.Children Databaseなど追加コンテンツ盛り沢山！</p>','2022-08-01','images/a5f643c8ce2ec324b80aa6b7af7e68bc.jpg','2022-08-02 05:55:47','2023-01-22 16:23:10',0),
	(2,'デビュー5周年に向けて音楽活動再開決定！','<p>Yukiは今年の12月4日にデビュー5周年を迎えます。</p>\r\n\r\n<p>デビュー5周年に向けて12月に音楽活動を再開することが決定！その第一歩として12月4日デビュー5周年記念日に配信限定シングル「光」をリリース。</p>\r\n\r\n<p>こちらは前作「Daydream」からおよそ2年振りの新曲となります。 「光」は今もまだ終わりが見えないコロナ禍への想い、そして未来への希望を込めた歌です。</p>\r\n\r\n<p>5周年のアニーバーサリーイヤーはこれだけではありません。 2023年には「BIRTH」から4年振りのオリジナルアルバムをリリース予定。 2020年から製作している楽曲に加えて、新たに書き下ろした新曲も収録。</p>\r\n\r\n<p>ぜひYukiの今後の活動をご期待ください！</p>\r\n\r\n<p><br />\r\n14th Single「光」<br />\r\n2022.12.04 Release</p>\r\n\r\n<ol>\r\n	<li>光</li>\r\n</ol>','2022-08-15',NULL,'2022-08-15 13:19:14','2025-01-24 22:35:52',0),
	(3,'2022 NEW SONG 制作決定！','<p>2022年初となるNEW SONGの制作が決定しました。<br />\r\nあと約2ヶ月で今年が終わります。 しかし、今年はまだ新曲を制作していなかったのです。</p>\r\n\r\n<p>5周年に向けて、動き出そうと考えている中で 新曲を制作するアイディアが浮かび上がりました。テーマは「今思うこと」 日々生活していく中で考えていること、残酷な現状、未来へ向かうための希望。そんな混沌とした思いを歌にします。<br />\r\n<br />\r\n来月にリリース予定の「光」の先に見えるものを描けたらなと思います。 実は「光」のデモは去年の8月に書き上げました。 ずっとレコーディングしたかった珠玉の楽曲です。 当時から1年以上経過し、その時とは違った環境、状況の中でまた違ったものが見えると思います。<br />\r\n<br />\r\nリリース日は全く未定ですが、 どうぞご期待ください。</p>','2022-11-11',NULL,'2022-11-11 21:54:11','2023-01-22 16:24:45',0),
	(5,'来春 New Singleリリース決定！','<p>2年ぶりのニューシングル「光」のリリースを前に早くも来春のNew Singleのリリースが決定！<br />\r\n<br />\r\n先日お知らせいたしました書き下ろしのNEW SONGと1年3ヶ月振りに降りてきた新境地をイメージさせるPop Song「Shadow」の両A面シングルとなります。<br />\r\n<br />\r\nさらに前作「BIRTH」リリースから2020年までに制作、リリースされた楽曲を詰め込んだ5th Albumも2023年内リリース予定。そして2022年以降に書き下ろした楽曲のみを収めた6th Albumのリリースも計画中！<br />\r\n<br />\r\nついに動き出したYukiに目が離せない！ 1</p>\r\n\r\n<hr />\r\n<p><br />\r\n5th Single「タイトル未定 / Shadow」<br />\r\n来春Release</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p>タイトル未定</p>\r\n	</li>\r\n	<li>\r\n	<p>Shadow</p>\r\n	</li>\r\n</ol>\r\n\r\n<p><br />\r\n5th Album「タイトル未定」<br />\r\n2023年内Release<br />\r\nシングル「Happy Birthday」「Highlight」「Sunshine」「Daydream」を含んだ全13曲。<br />\r\n<br />\r\n6th Album「タイトル未定」<br />\r\nリリース日未定<br />\r\nシングル「光」「タイトル未定」「Shadow」「僕の好きな場所へ (未発表曲)」を含んだ全10曲。</p>','2022-11-25',NULL,'2022-11-25 16:46:10','2024-03-18 23:59:24',1),
	(6,'「光」リリース延期のお知らせ','<p>12月4日リリース予定のシングル「光」のリリース日を延期することが決定いたしました。<br />\r\n<br />\r\n作詞・作曲を含む楽曲制作、レコーディングが間に合わないための延期となります。年内リリースを予定しておりますので、もう少しお待ちくださいませ。</p>','2022-11-29',NULL,'2022-11-29 13:18:02','2023-01-22 16:30:53',0),
	(7,'5th Anniversary Year！','<p>2022年12月4日にデビュー5周年を迎えました。<br />\r\n皆さんのおかげで5周年を迎えることができました。ありがとうございます。<br />\r\n今後もYukiの応援をよろしくお願いいたします。</p>','2022-12-08',NULL,'2022-12-08 10:14:26','2024-03-18 23:36:53',0),
	(8,'音楽活動再始動延期のお知らせ','<p>2022年12月6日にデビュー5周年を迎えました。<br />\r\n来年が5周年イヤーとして、NEW ALBUM、そしてBEST ALBUMをリリース予定のため、年内は活動をお休みし、来年から音楽活動を再始動することに決定いたしました。<br />\r\nそのため、配信シングル「光」も来年のリリースとなりますため、 もう少しお待ちいただけると幸いです。 今後ともよろしくお願いいたします。</p>','2022-12-22',NULL,'2022-12-22 09:42:45','2024-03-18 23:36:18',0),
